+            $pilihan .= $select[$i];
+        }
+        else{
+            $pilihan .= $select[$i].", ";
+        }
+    }
+    $sql = "update kategori_asset set nama = '".$group."', assignto = '".$pilihan."', description = '".$description."' where id = '".$id."'";
